mapper.select:
select coupon.coupon_id, coupon.coupon_name, coupon.coupon_remark, coupon.coupon_start_time, coupon.coupon_end_time, coupon.create_time, coupon.modify_time, coupon.del_flag, coupon.business_id, coupon.coupon_integral, coupon.coupon_is_show, coupon.coupon_pic,coupon.coupon_rules_type ,no.code_id, no.code_no, no.customer_id, no.code_status, no.code_get_type, no.code_get_time, no.code_use_order_id,store.store_name
from np_coupon coupon INNER JOIN np_coupon_no no on coupon.coupon_id = no.coupon_id left join np_third_store_info store on store.store_id=coupon.business_id where coupon.del_flag = '0' and no.customer_id = #{customerId} and no.code_status != 0
and no.code_status = #{codeStatus}
and coupon.coupon_start_time <= now() and coupon.coupon_end_time > now()
limit #{start},#{number}

select coupon.coupon_id, coupon.coupon_name, coupon.coupon_remark, coupon.coupon_start_time, coupon.coupon_end_time, coupon.create_time, coupon.modify_time, coupon.del_flag, coupon.business_id, coupon.coupon_integral, coupon.coupon_is_show, coupon.coupon_pic,coupon.coupon_rules_type ,no.code_id, no.code_no, no.customer_id, no.code_status, no.code_get_type, no.code_get_time, no.code_use_order_id,store.store_name from np_coupon coupon INNER JOIN np_coupon_no no on coupon.coupon_id = no.coupon_id left join np_third_store_info store on store.store_id=coupon.business_id where coupon.del_flag = '0' and no.customer_id = #{customerId} and no.code_status != 0 and no.code_status != '2'
and coupon.coupon_end_time < now()
limit #{start},#{number}

select count(1)
from np_coupon coupon ,np_coupon_no no where coupon.coupon_id = no.coupon_id and coupon.del_flag = '0' and no.customer_id = #{customerId}
and coupon.coupon_end_time < now()


select count(*)

from np_coupon coupon ,np_coupon_no no 
where coupon.coupon_id = no.coupon_id and coupon.del_flag = '0' 
and no.customer_id = #{customerId} 

        and no.code_status = #{codeStatus}


    and   coupon.coupon_start_time <= now() and  coupon.coupon_end_time  > now()

select
from np_coupon where del_flag = '0' and coupon_is_show = '1' and coupon_id in
#{couponId,jdbcType=BIGINT}

select 

from np_coupon
where coupon_id = #{couponId,jdbcType=BIGINT}

select 
,
(select count(1)  from np_coupon_no no where no.coupon_id = n.coupon_id and no.code_status = '1') coupon_getted_no
from np_coupon n
where 1=1 

  and n.del_flag = #{delFlag,jdbcType=CHAR}


  and n.coupon_name like CONCAT(CONCAT('%',#{couponName,jdbcType=VARCHAR},'%'))  


  and n.business_id  =  #{businessId,jdbcType=BIGINT}


  and n.coupon_is_show  =  #{couponIsShow,jdbcType=CHAR}


    and n.coupon_start_time  > '#{couponStartTime}'



    and n.coupon_end_time   < '#{couponEndTime}'


    and business_id  =  #{businessId,jdbcType=BIGINT}


    and business_id=0



order by n.create_time desc
limit #{start},#{number}

select 

from np_coupon
where coupon_id = #{couponId,jdbcType=BIGINT}

mapper.insert:
insert into np_coupon (coupon_id, coupon_name, coupon_remark, 
  coupon_start_time, coupon_end_time, create_time, 
  modify_time, del_flag, business_id, 
  coupon_integral, coupon_is_show, coupon_pic
  )
values (#{couponId,jdbcType=BIGINT}, #{couponName,jdbcType=VARCHAR}, #{couponRemark,jdbcType=VARCHAR}, 
  #{couponStartTime,jdbcType=TIMESTAMP}, #{couponEndTime,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}, 
  #{modifyTime,jdbcType=TIMESTAMP}, #{delFlag,jdbcType=CHAR}, #{businessId,jdbcType=BIGINT}, 
  #{couponIntegral,jdbcType=BIGINT}, #{couponIsShow,jdbcType=CHAR}, #{couponPic,jdbcType=VARCHAR}
  )

mapper.update:
update np_coupon
set del_flag = '1'
where coupon_id = #{couponId,jdbcType=BIGINT}

update np_coupon
set del_flag = '1'
where coupon_id = #{couponId,jdbcType=BIGINT} 

and business_id = #{businessId,jdbcType=BIGINT}

update np_coupon
set del_flag = '1'
where coupon_id in 

   #{couponId,jdbcType=BIGINT}


and business_id = #{businessId,jdbcType=BIGINT}
