mapper.select:
select goods_id,goods_name,goods_subtitle,goods_keywords,goods_no,goods_added,goods_price,goods_img,goods_brief, goods_recom,goods_deno,third_cate_id,audit_status,goods_seo_title,goods_seo_keyword,goods_seo_desc, goods_prom,goods_info_instocksell,goods_usecoupon,goods_detail_desc,mobile_desc,goods_delflag from np_goods where goods_delflag = '0' and goods_added = '1'
and goods_name = #{productNo} or goods_name like CONCAT(CONCAT('%', #{productNo}),'%')
and third_id = #{thirdId}
order by goods_create_time limit #{startRowNum},#{endRowNum}

select count(1) from np_goods where goods_delflag = '0' and goods_added = '1'
and goods_name = #{productNo} or goods_name like CONCAT(CONCAT('%', #{productNo}),'%')
and third_id = #{thirdId}

select goods.goods_id, cat_id, type_id, goods_name, goods_subtitle, goods_no, goods_keywords, brand_id, goods_brief, goods_added, goods_uptime, goods_price, goods_recom, goods_img, goods_score, goods_deno, goods_seo_title, goods_seo_keyword, goods_seo_desc, goods_prom, goods_info_instocksell, goods_usecoupon, goods_create_name, goods_create_time, goods_modified_name, goods_modified_time, goods_del_name, goods_del_time, goods_delflag,goods_belo,goods_belo_name,goods.is_third,third_cate_id,is_third_audit_used,goods.audit_status,freight_template_id,(SELECT goods_info_id FROM np_goods_info info WHERE info.goods_id=goods.goods_id LIMIT 0,1) as goods_info_id from np_goods goods ,np_goods_info inf RIGHT JOIN (select product_id, np_product_ware.ware_stock from np_product_ware WHERE np_product_ware.del_flag='0' and ware_stock = 0) ware on ware.product_id = inf.goods_info_id where inf.goods_id = goods.goods_id and goods.goods_delflag='0'
and goods_name like CONCAT(CONCAT('%', #{searchBean.goodsName}),'%')
and goods_no = #{searchBean.goodsNo}
and cat_id = #{searchBean.goodsCateId}
and brand_id = #{searchBean.goodsBrandId}
and goods_added = #{searchBean.status}
and goods_name like CONCAT(CONCAT('%', #{searchBean.searchText}),'%')
and goods_no = #{searchBean.searchText}
and goods_keywords like CONCAT(CONCAT('%', #{searchBean.searchText}),'%')
and goods.is_third= #{searchBean.isThird}
and goods_belo_name like CONCAT(CONCAT('%', #{searchBean.thirdName}),'%')
and audit_status =#{searchBean.auditStatus}
and third_id = #{searchBean.thirdId}
group by goods.goods_id order by goods.goods_create_time desc limit #{startRowNum},#{endRowNum}

select goods.goods_id, cat_id, type_id, goods_name, goods_subtitle, goods_no, goods_keywords, brand_id, goods_brief, goods_added, goods_uptime, goods_price, goods_recom, goods_img, goods_score, goods_deno, goods_seo_title, goods_seo_keyword, goods_seo_desc, goods_prom, goods_info_instocksell, goods_usecoupon, goods_create_name, goods_create_time, goods_modified_name, goods_modified_time, goods_del_name, goods_del_time, goods_delflag,goods_belo,goods_belo_name,goods.is_third,third_cate_id,is_third_audit_used,goods.audit_status,freight_template_id,(SELECT goods_info_id FROM np_goods_info info WHERE info.goods_id=goods.goods_id LIMIT 0,1) as goods_info_id from np_goods goods ,np_goods_info inf RIGHT JOIN (select product_id, np_product_ware.ware_stock from np_product_ware WHERE np_product_ware.del_flag='0' and ware_stock < #{searchBean.swValue}) ware on ware.product_id = inf.goods_info_id where inf.goods_id = goods.goods_id and goods.goods_delflag='0'
and goods_name like CONCAT(CONCAT('%', #{searchBean.goodsName}),'%')
and goods_no = #{searchBean.goodsNo}
and cat_id = #{searchBean.goodsCateId}
and brand_id = #{searchBean.goodsBrandId}
and goods_added = #{searchBean.status}
and goods_name like CONCAT(CONCAT('%', #{searchBean.searchText}),'%')
and goods_no = #{searchBean.searchText}
and goods_keywords like CONCAT(CONCAT('%', #{searchBean.searchText}),'%')
and goods.is_third= #{searchBean.isThird}
and goods_belo_name like CONCAT(CONCAT('%', #{searchBean.thirdName}),'%')
and audit_status =#{searchBean.auditStatus}
and third_id = #{searchBean.thirdId}
group by goods.goods_id order by goods.goods_create_time desc limit #{startRowNum},#{endRowNum}

select count(DISTINCT goods.goods_id)
from np_goods goods ,np_goods_info inf
RIGHT JOIN (select product_id, np_product_ware.ware_stock from np_product_ware WHERE np_product_ware.del_flag='0' and ware_stock < #{swValue}) ware
on ware.product_id = inf.goods_info_id
where inf.goods_id = goods.goods_id and goods.goods_delflag='0'

    and goods.goods_name like CONCAT(CONCAT('%',
    #{goodsName}),'%')


    and goods.goods_no = #{goodsNo}


    and goods.cat_id  = #{goodsCateId}


    and goods.brand_id = #{goodsBrandId}


    and goods.goods_added = #{status}


    and goods.goods_name like CONCAT(CONCAT('%',
    #{searchText}),'%')


    and goods.goods_no = #{searchText}


    and goods.goods_keywords like CONCAT(CONCAT('%',
    #{searchText}),'%')


    and goods.is_third= #{isThird}


    and goods_belo_name like CONCAT(CONCAT('%',
    #{thirdName}),'%')


    and goods.audit_status = #{auditStatus}

select count(DISTINCT goods.goods_id)
from np_goods goods ,np_goods_info inf
RIGHT JOIN (select product_id, np_product_ware.ware_stock from np_product_ware WHERE np_product_ware.del_flag='0' and ware_stock = 0 ) ware
on ware.product_id = inf.goods_info_id
where inf.goods_id = goods.goods_id and goods.goods_delflag='0'

    and goods.goods_name like CONCAT(CONCAT('%',
    #{goodsName}),'%')


    and goods.goods_no = #{goodsNo}


    and goods.cat_id  = #{goodsCateId}


    and goods.brand_id = #{goodsBrandId}


    and goods.goods_added = #{status}


    and goods.goods_name like CONCAT(CONCAT('%',
    #{searchText}),'%')


    and goods.goods_no = #{searchText}


    and goods.goods_keywords like CONCAT(CONCAT('%',
    #{searchText}),'%')


    and goods.is_third= #{isThird}


    and goods.goods_belo_name like CONCAT(CONCAT('%',
    #{thirdName}),'%')


    and goods.audit_status = #{auditStatus}
