$(function () {
    jQuery.fn.isChildAndSelfOf = function (e) {
        return this.closest(e).length > 0
    },
    $('#slides').length > 0 && $('#slides').slidesjs({
        width: 1000,
        height: 300,
        play: {
            active: !1,
            effect: 'fade',
            auto: !0,
            interval: 4000,
            pauseOnHover: !0,
            restartDelay: 2500
        },
        navigation: {
            active: !0
        },
        pagination: {
            active: !1,
            effect: 'fade'
        }
    }),
        $(".slides").bind("mouseenter",function(){
            $(".slidesjs-previous, .slidesjs-next").fadeIn();
        }),
        $(".slides").bind("mouseleave",function(){
            $(".slidesjs-previous, .slidesjs-next").fadeOut();
        }),

        $(".app-tabs a").click(function(){
            var e = $(this);
            _time = setTimeout(function () {
                var n = e.parent("li").index(), s = 90 * n;
                $(".underline").animate({left: s}, 50), e.addClass("on").parent("li").siblings().find(".on").removeClass("on"), $(".app-lists").find(".on").removeClass("on"), $(".app-list:eq(" + n + ")").addClass("on")
            }, 50)
        }),
        $('.select-menu strong').click(function () {
            $(this).next('.pop-menu').show()
        }),
        $(document).click(function (e) {
            $(e.target).isChildAndSelfOf('.select-menu') || $('.pop-menu').slideUp('fast')
        }),
        $('.pop-menu a').click(function () {
            var e = $(this).text();
            $(this).addClass('selected').siblings('.selected').removeClass('selected'),
                $(this).parent('.pop-menu').slideUp('fast').prev('strong').find('span').text(e)
        }),
        $('.menu-down').click(function () {
            var e = $(this),
                n = e.parent().next('.extra-menu');
            $('.extra-menu:visible').slideUp('fast'),
                'block' == n.css('display') ? n.slideUp('fast')  : n.show()
        }),
        $(document).click(function () {
            $(event.target).isChildAndSelfOf('.menu-down') || $('.extra-menu').slideUp('fast')
        }),
        $('.app-img, .app-infomation').click(function () {
            $('.dia-mask, .app-dialog').fadeIn('fast')
        }),
        $('.dia-close').click(function () {
            $('.dia-mask, .app-dialog').fadeOut('fast')
        })
});
