mapper.select:
select brand_id,brand_logo,brand_nickname,brand_name,brand_url,brand_sort from np_goods_brand where 1=1 and brand_delflag = "0" and store_id is null and brand_id not in ( select brand_id from np_third_grandbrand WHERE third_id = #{thirdId} and (rate_status = '1' or rate_status = '0'))
and brand_name like CONCAT(CONCAT('%',#{brandName,jdbcType=VARCHAR},'%'))
GROUP BY brand_id order by brand_name limit #{startRowNum},#{endRowNum}

mapper.update:
	 update np_third_grandbrand set del_flag='1' , rate_status='2'
	 where brand_id=#{brandId,jdbcType=BIGINT}
   and third_id=#{thirdId,jdbcType=BIGINT}

	 update np_third_grandbrand set del_flag='1' , rate_status='2'  
	 where brand_id in 

      #{brandId}   

    and third_id=#{thirdId,jdbcType=BIGINT}
