select
from np_shopping_cart where del_flag = '0' and shopping_cart_id in
#{shoppingCartId,jdbcType=BIGINT}

SELECT sg.shopping_cart_id, sg.goods_info_weight,sg.goods_info_id, sg.goods_num, sg.fit_id,sg.third_id, sg.customer_id, sg.goods_id,sg.marketing_id,sg.goods_group_id, sg.shopping_cart_time, sg.del_flag,sg.distinct_id,sg.marketing_activity_id,sg.order_marketing_id from (select shopping_cart_id, info.goods_info_weight,cart.goods_info_id, goods_num, fit_id,info.third_id, customer_id, info.goods_id,goods_group_id, marketing_id, shopping_cart_time, del_flag,distinct_id,marketing_activity_id,order_marketing_id from np_shopping_cart cart LEFT JOIN np_goods_info info on info.goods_info_id = cart.goods_info_id where cart.del_flag = '0' and cart.shopping_cart_id in
#{shoppingCartId,jdbcType=BIGINT}
) sg LEFT JOIN np_goods gd on gd.goods_id=sg.goods_id

SELECT SUM(sg.goods_info_weight) goods_info_weight,SUM(sg.goods_num) goods_num,sg.freight_template_id from (SELECT sc.goods_num, gd.goods_info_weight,good.freight_template_id from np_shopping_cart sc,np_goods_info gd,np_goods good where gd.goods_info_id =sc.goods_info_id and good.goods_id=gd.goods_id and gd.goods_info_delflag='0' and good.goods_delflag='0' and sc.del_flag='0' and sc.shopping_cart_id in
#{shopcartIds,jdbcType=BIGINT}
) sg where sg.freight_template_id is not null GROUP BY sg.freight_template_id

SELECT sum(sc.goods_num) goods_num, gd.goods_info_weight ,sc.marketing_activity_id ,codex.codex_type, Sum((sc.goods_num*goods_info_prefer_price)) goods_price, SUM(sc.goods_num*pdm.discount_price) market_goodsprice from np_shopping_cart sc LEFT OUTER JOIN np_pre_discount_marketing pdm on sc.marketing_id = pdm.marketing_id and pdm.goods_id=sc.goods_info_id, np_goods_info gd,np_marketing m, np_marketing_codex mc,np_codex codex where gd.goods_info_id =sc.goods_info_id and gd.goods_info_delflag='0' and sc.del_flag='0' and m.marketing_id = mc.marketing_id and mc.codex_id = codex.codex_id and mc.flag='0'and sc.marketing_activity_id=m.marketing_id and m.marketing_begin <= now() and m.marketing_end > now() and sc.shopping_cart_id in
#{shopcartIds,jdbcType=BIGINT}
group by sc.marketing_activity_id

