mapper.update:
		update np_set 

          is_backorder = #{isBackOrder,jdbcType=CHAR}



          receipt_time = #{receiptTime,jdbcType=BIGINT}



          limit_order_time = #{limitOrderTime,jdbcType=BIGINT}



          kuaidi100key = #{kuaidi100key,jdbcType=VARCHAR}


    where set_id = #{setId,jdbcType=BIGINT}

mapper.select:
select from np_sys_advertisement a inner join np_sys_advertype t on a.at_id = t.at_id where a.del_flag='0' order by t.at_id,a.at_position,a.adver_sort
and a.adver_name like CONCAT(CONCAT('%', #{searchText},'%'))
limit #{startRowNum},#{endRowNum}
