mapper.update:
UPDATE NP_SYS_STA_CODE




			STA_CODE_ID = #{staCodeId,
					jdbcType=BIGINT
			},



			STA_TITLE = #{staTitle,
					jdbcType=VARCHAR
			},



			STA_STYLE = #{staStyle,
					jdbcType=VARCHAR
			},



			MODULE = #{module,
					jdbcType=VARCHAR
			},



			CODE = #{code,
					jdbcType=VARCHAR
			},



			USED_STATUS = #{usedStatus,
					jdbcType=VARCHAR
			},



			INSERT_ID = #{insertId,
					jdbcType=BIGINT
			},



			INSERT_DATE = #{insertDate,
					jdbcType=DATE
			},



			MODIFY_ID = #{modifyId,
					jdbcType=BIGINT
			},



			MODIFY_DATE = #{modifyDate,
					jdbcType=DATE
			},



			DELETE_STATUS = #{deleteStatus,
					jdbcType=BIGINT
			},


WHERE 
		STA_CODE_ID = #{staCodeId,
					jdbcType=BIGINT
			}

