mapper.select:
    select  from np_channel_storey_goods
    where delflag = '0' and user_status = '1'

		and storey_id = #{storeyId,jdbcType=BIGINT}


		and storey_tag_id = #{storeyTagId,jdbcType=BIGINT}


		and temp1 = #{temp1,jdbcType=VARCHAR}


		and temp2 = #{temp2,jdbcType=VARCHAR}


      and is_hot = #{isHot,jdbcType=VARCHAR}


          and temp4 = #{temp4,jdbcType=VARCHAR}

    ORDER BY sort

select ,IFNULL(w.ware_stock,0) AS ware_stock, IFNULL(w.ware_price,g.goods_info_prefer_price) AS ware_price from np_channel_storey_goods i inner join np_goods_info g on g.goods_info_id=i.goodsproduct_id
left join (select sum(goods_info_num) as count, og.goods_info_id from np_order_goods og left join (select order_id from np_order where order_status='3') os on og.order_id = os.order_id group by goods_info_id) o on o.goods_info_id=i.goodsproduct_id
left join (select count(1) as attecount,goods_id from np_customer_follow group by goods_id) a on a.goods_id=i.goodsproduct_id
left join (select ware_stock,product_id,ware_price from np_product_ware p where p.ware_id in (select c.ware_id from np_ware_city c,np_warehouse h where h.ware_id=c.ware_id and c.city_id=#{distinctId} AND h.del_flag='0' and c.del_flag='0') and p.del_flag='0' GROUP BY p.product_id ) w on w.product_id = i.goodsproduct_id where i.delflag = '0' and i.user_status = '1'
and i.storey_id = #{storeyId,jdbcType=BIGINT}
and i.storey_tag_id = #{storeyTagId,jdbcType=BIGINT}
and i.temp1 = #{temp1,jdbcType=VARCHAR}
and i.temp2 = #{temp2,jdbcType=VARCHAR}
and i.is_hot = #{isHot,jdbcType=VARCHAR}
and i.temp4 = #{temp4,jdbcType=VARCHAR}
and w.ware_stock>0
order by i.goodsproduct_price
order by i.goodsproduct_price desc
order by o.count desc
order by o.count
order by i.create_date
order by i.create_date desc
order by a.attecount
order by a.attecount desc
LIMIT #{startRowNum,jdbcType=BIGINT} , #{endRowNum,jdbcType=BIGINT}

select count(i.channel_storey_goodsproduct_id) from np_channel_storey_goods i inner join np_goods_info g on g.goods_info_id=i.goodsproduct_id
left join (select sum(goods_info_num) as count, og.goods_info_id from np_order_goods og left join (select order_id from np_order where order_status='3') os on og.order_id = os.order_id group by goods_info_id) o on o.goods_info_id=i.goodsproduct_id
left join (select count(1) as attecount,goods_id from np_customer_follow group by goods_id) a on a.goods_id=i.goodsproduct_id
where i.delflag = '0' and i.user_status = '1'
and i.storey_id = #{storeyId,jdbcType=BIGINT}
and i.is_hot = #{isHot,jdbcType=VARCHAR}
and i.storey_tag_id = #{storeyTagId,jdbcType=BIGINT}
and i.temp1 = #{temp1,jdbcType=VARCHAR}
and i.temp2 = #{temp2,jdbcType=VARCHAR}
and i.temp4 = #{temp4,jdbcType=BIGINT}
order by i.goodsproduct_price
order by i.goodsproduct_price desc
order by o.count desc
order by o.count
order by i.create_date
order by i.create_date desc
order by a.attecount
order by a.attecount desc

