mapper.select:
select
,
from np_mob_home_page where isthird ='1' and store_id = #{storeId}

mapper.update:
update np_mob_home_page set temp2='1',update_date=#{updateDate,jdbcType=TIMESTAMP}
where homepage_id = #{homepageId,jdbcType=BIGINT} 
and merchant_id= #{merchantId,jdbcType=BIGINT}

mapper.select:
select
from np_mob_home_page WHERE 1=1
ORDER BY SORT,CREATE_DATE DESC limit #{startRowNum,jdbcType=BIGINT} , #{endRowNum,jdbcType=BIGINT}
